import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode'; // Assuming jwtDecode is properly imported

const RestaurantDashboard = () => {
    const [restaurant, setRestaurant] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Check for JWT token
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login'); // Redirect to login if no token is found
            return;
        }

        // Decode the token to verify if the user is a restaurant
        const decodedToken = jwtDecode(token);
        if (decodedToken.role !== 'Restaurant') {
            navigate('/login'); // Redirect if the logged-in user is not a restaurant
            return;
        }

        // Fetch restaurant data
        const fetchRestaurantData = async () => {
            try {
                const response = await axios.get('/api/restaurant', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setRestaurant(response.data);
            } catch (error) {
                console.error('Error fetching restaurant data:', error);
                navigate('/login'); // Redirect on error (e.g., token expired)
            }
        };

        fetchRestaurantData();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (!restaurant) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Welcome, {restaurant.ownerName}!</h1>

            <section>
                <h2>Restaurant Details</h2>
                {restaurant.logo && (
                    <img
                        src={`http://localhost:5000/uploads/${restaurant.logo}`}
                        alt="Restaurant Logo"
                    />
                )}
                <p><strong>Restaurant Name:</strong> {restaurant.restaurantName}</p>
                <p><strong>Email:</strong> {restaurant.email}</p>
                <p><strong>Phone:</strong> {restaurant.phone}</p>
                <p><strong>Address:</strong> {restaurant.address}</p>
                <p><strong>Delivery Radius:</strong> {restaurant.deliveryRadius} km</p>
            </section>

            <section>
                <h2>Menu Management</h2>
                <ul>
                    {restaurant.menuItems && restaurant.menuItems.length > 0 ? (
                        restaurant.menuItems.map((item, index) => (
                            <li key={index}>
                                <strong>{item.name}</strong> - ${item.price}
                            </li>
                        ))
                    ) : (
                        <li>No menu items available.</li>
                    )}
                </ul>
                <a href="/menu">Edit Menu</a>
            </section>

            <section>
                <h2>Order History</h2>
                <p>No orders yet.</p>
            </section>

            <button onClick={handleLogout}>Log Out</button>
        </div>
    );
};

export default RestaurantDashboard;
